import jxl.*;
import jxl.write.*
import groovy.json.JsonSlurper

import com.eviware.*

def response = context.testCase.getTestStepByName("GetAllProducts").getPropertyValue("response")

def holder = new JsonSlurper().parseText(response)

def product = holder.data

for(def m=0;m<product.size();m++)
{

	if(product[m].quantity<=10)
	{
		def idtoupdate = product[m].id.toString();
		def qtytoupdate = product[m].quantity.toString();
		context.testCase.setPropertyValue("id",idtoupdate)
		context.testCase.setPropertyValue("quantity",qtytoupdate)

		testRunner.gotoStepByName("UpdateProductByQuantity")
		log.info "Updated Successfully"

	}

	else
	{
		log.info "Update unsuccessful"
	}
}

