import groovy.sql.Sql

//connection Driver, username password, URL

def url ='jdbc:mysql://localhost:3306/rest';

def user='root'
def password='root'
def driver ='com.mysql.jdbc.Driver'

// Register to MySQL  JDBC Driver - For Groovy to send and Recieve

com.eviware.soapui.support.GroovyUtils.registerJdbcDriver(driver)

//connect to SQL Instance

def sql = Sql.newInstance(url,user,password,driver)

sql.eachRow("select * from Category_product")
{
	row ->
	def Category ="$row.category";
	def product_key ="$row.product_key";
	def quantity ="$row.quantity";
	testRunner.testCase.testSuite.testCases['TC26'].setPropertyValue("Category",Category)
     testRunner.testCase.testSuite.testCases['TC26'].setPropertyValue("product_key",product_key)
     testRunner.testCase.testSuite.testCases['TC26'].setPropertyValue("quantity",quantity)
   def tc=  testRunner.testCase.testSuite.testCases['TC26']
   tc.run(null, false)

}

sql.close()

